import React , {useState , useEffect } from "react";
//import request from 'request';

export default function Map(){
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    const place = {
      lat:25.013308495488523, 
      lng :121.54106875361296
    }
  // Note: the empty deps array [] means
  // this useEffect will run once
  // similar to componentDidMount()
  useEffect(() => {
    //fetch("https://www.googleapis.com/youtube/v3/search?part=snippet&q=test&key=AIzaSyCyQwvOXSM2IwbI-D6ub-u230D7p6NT6u4&type=video&maxResults=3" , 
    fetch("http://127.0.0.1:8000/picture" , 
        {
        headers: {
          'Content-Type': 'image/jpg',
        }
      })
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
          console.log(items)
        },

        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])



const map = {
  width :'100px',
  height : '100px'

}

  if (error) {
    return <img 
    className ={map} 
    src={'http://127.0.0.1:8000/picture'}
    alt= ' ' />;
  } 
  else if (!isLoaded) {
    return <div>Loading...</div>;
  } 
  else {
    return (
      <div>
        <img src={{uri : 'http://127.0.0.1:8000/map/?lat=25.013362460447322&lng=121.54140203728495'}} alt= ' ' />

      </div>


    );
  }



}
