import React ,{Component}from 'react';
import { GoogleMap, useJsApiLoader } from '@react-google-maps/api';
const initialize = () =>{
    function success(position){
        var latitude = position.coords.latitude;
        var longitude = position.coords.longitude;
        var yourmap = {
            center:new GoogleMap.maps.LatLng(latitude , longitude),
            zoom:20,
            mapTypeId:GoogleMap.maps.MapTypeId.ROADMAP
        };
        var map = new GoogleMap.maps.Map(document.getElementById("googleMap"), yourmap);
        var marker = new GoogleMap.maps.Marker({position:new google.maps.LatLng(latitude,longitude)});
        marker.setMap(map);
        var infowindow = new GoogleMap.maps.InfoWindow({content:"i'm here!"});
        infowindow.open(map,marker)
    };

    function error(){
        alert('the position is not allowed')
    };

    var geo_options = {
        enableHighAccuracy : true,
        maximumAge:30000,
        timeout:27000
    };

    if("geolocation" in navigator){
        navigator.geolocation.watchPosition(success,error,geo_options)

    }else{
        alert('the position is not allowed')
    }
};

class Map extends Component {
    render(){
    return(
<div>
    <div>
        <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyAj6NGGgE6guMZsGqLod-WTNXcJGwq-DAs&sensor=false"></script>
        <script> google.maps.event.addDomListener(window, 'load' , initialize); </script>
    </div>
    <div>
        <div id="googleMap" style="width:500px;height:380px;"></div>
    </div>
</div>
    )
}
}

export default Map
