import React from 'react';

const FunctionalComponent=()=>{
    const [count, setCount] = React.useState(0);


        const increase = () => {
            if(count > 10){
                count =0
           }else{
            setCount(count+1);}
        }



    return (
        <div style={{margin:'50px'}}>
            <h1>Welcome to Geeks for Geeks </h1>
            <h3>Counter App using Functional Component : </h3>
          <h2>{count}</h2>
            <button onClick={increase}>Add</button>
        </div>
    )
}  

const TableHeader = () => {
    return (
      <thead>
        <tr>
          <th>Name</th>
          <th>Job</th>
        </tr>
      </thead>
    )
  }

  const TableBody = () => {
    return (
      <tbody>
        <tr>
          <td>Charlie</td>
          <td>Janitor</td>
        </tr>
        <tr>
          <td>Mac</td>
          <td>Bouncer</td>
        </tr>
        <tr>
          <td>Dee</td>
          <td>Aspiring actress</td>
        </tr>
        <tr>
          <td>Dennis</td>
          <td>Bartender</td>
        </tr>
      </tbody>
    )
  }

  class Table extends React.Component {
    render() {
      return (
        <table>
          <TableHeader />
          <TableBody />
            <FunctionalComponent />
        </table>
      )
    }
  }

  export default Table;
