import React ,{useMemo} from 'react';
import { GoogleMap , useLoadScript , Marker} from '@react-google-maps/api';


export default function App(){

  const {isLoaded } = useLoadScript({
    googleMapsApiKey :"AIzaSyAj6NGGgE6guMZsGqLod-WTNXcJGwq-DAs",

  });
  function success(position){
    var latitude = position.coords.latitude;
    var longitude = position.coords.longitude;
    var yourmap = {
        center:new google.maps.LatLng(latitude , longitude),
        zoom:20,
        mapTypeId:google.maps.MapTypeId.ROADMAP
    };
    var map = new google.maps.Map(document.getElementById("googleMap"), yourmap);
    var marker = new google.maps.Marker({position:new google.maps.LatLng(latitude,longitude)});
    marker.setMap(map);
    var infowindow = new google.maps.InfoWindow({content:"i'm here!"});
    infowindow.open(map,marker);

};
  if(!isLoaded) return <div>Loading...</div>;
  return <Map/>;

}
const containerStyle = {
  width: '100%',
  height: '100vh'
};


function Map(){
  const center = useMemo(() => ({lat : 25.014 , lng :121.545}) , []);
  return (
  <GoogleMap 
    zoom={16} 
    center={center}
    mapContainerStyle={containerStyle}
  >
    <Marker position= {center}/>

   </GoogleMap> 

    )
}
