import React ,{Component}from 'react';
import { GoogleMap, useJsApiLoader } from '@react-google-maps/api';

const containerStyle = {
  width: '1400px',
  height: '800px'
};

const center = {
  lat: position.coords.latitude, 
  lng: position.coords.longitude
};

const [map, setMap] = React.useState(null);

class MyComponent extends Component {

    constructor() {
        super();
        this.state = {
            center: center,
            zoom: 16,
            features: [],
        };
    }

    isLoaded  = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyAj6NGGgE6guMZsGqLod-WTNXcJGwq-DAs"
  })

   getLocation = () => {
    if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(this.updateLocation);
    }
}

 updateLocation = (position) => {
    this.setState({
        center: {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
        }
    }, () => {
        // console.log(this.map);
        // 定位完成後，刪掉起始位置的marker
        this.marker.setMap(null);
        // 新增現在位置的marker
        this.marker = new GoogleMap.maps.Marker({
            position: new GoogleMap.maps.LatLng(this.state.center.lat, this.state.center.lng),
            map: this.map,

        });
        this.map.setCenter(this.state.center);
        this.map.setZoom(16);  
    });
}



   onLoad = React.useCallback(function callback(map) {
    const bounds = new window.google.maps.LatLngBounds(center);
    map.fitBounds(bounds);
    setMap(map)
  }, [])

   onUnmount = React.useCallback(function callback(map) {
    setMap(null)
  }, [])
   num = 0;

  render(){
  return isLoaded? (
    <body>
      <div><h1>hello</h1><button onClick={(num) => num +1}>{num}</button></div>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={10}
        onLoad={onLoad}
        onUnmount={onUnmount}
      >
        { /* Child components, such as markers, info windows, etc. */ }
        <></>
      </GoogleMap>

      </body>
  ) : <></>
  }
}

export default MyComponent;
