import React ,{ useEffect } from "react"
//import { Helmet } from "react-helmet"

function Map() {
  useEffect(() => {
   const script = document.createElement("script")

    script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyAj6NGGgE6guMZsGqLod-WTNXcJGwq-DAs&sensor=false";

    script.async = true
    document.body.appendChild(script)

    return () => {
             // clean up the script when the component in unmounted
              document.body.removeChild(script)
            }

  }, [])

  function initialize()
{
    const google = window.google;
    function success(position){
        var latitude = position.coords.latitude;
        var longitude = position.coords.longitude;
        var yourmap = {
            center:new google.maps.LatLng(latitude , longitude),
            zoom:20,
            mapTypeId:google.maps.MapTypeId.ROADMAP
        };
        var map = new google.maps.Map(document.getElementById("googleMap"), yourmap);
        var marker = new google.maps.Marker({position:new google.maps.LatLng(latitude,longitude)});
        marker.setMap(map);
        var infowindow = new google.maps.InfoWindow({content:"i'm here!"});
        infowindow.open(map,marker);

    };

    function error(){
        alert('the position is not allowed');
    };
    var geo_options = {
        enableHighAccuracy: true,
        maximumAge:30000,
        timeout:27000
    };

    if("geolocation" in navigator){
        navigator.geolocation.watchPosition(success,error,geo_options);

    }else{
        alert('the position is not allowed');
    }
};
google.maps.event.addDomListener(window, 'load' , initialize);

  return <div className="App"></div>
}

export default Map
