import React ,{useMemo} from 'react';
import { GoogleMap , useLoadScript , Marker} from '@react-google-maps/api';

class Direct extends React.Component{
  constructor(props){
    super(props);
    this.state={
      latitude:null,
      longitude:null,
      userAddress:null
    };
    this.getLocation = this.getLocation.bind(this);
    this.getCoordinates = this.getCoordinates.bind(this);
  }

  getLocation(){
    if(navigator.geolocation){
      navigator.geolocation.getCurrentPosition(this.getCoordinates , this.handleLocationError);

    }else{
      alert("Geolocaiton is not supported in the browser")
    }
  }

  getCoordinates(position){
    this.setState({
      latitude: position.coords.latitude,
      longitude:position.coords.longitude
    })
  }


  handleLocationError(error){
    switch(error.code){
      case error.PERMISSION_DENIED:
        alert("User denied the request for Geolocation")
        break;
      case error.POSITION_UNAVAILABLE:
        alert('Location information is unavailable.')
        break;
      case error.TIMEOUT:
        alert("The request to get user location timed out.")
        break;
      case error.UNKNOWN_ERROR:
        alert('An unknown error occurred.')
        break;
      default:
        alert('An unknown error occurred.')
    }
  }

  /*render(){
    return (
      <div className="App">
        <h2>React Geolocation Example</h2>
        <button onClick={this.getLocation}>get location</button>
        <h4>html</h4>
        <p>latitude:{this.state.latitude}</p>
        <p>longitude:{this.state.longitude}</p>

      </div>
    )
  }*/
}


const direct = new Direct();

export default function App(){

  direct.getLocation();
  const {isLoaded } = useLoadScript({
    googleMapsApiKey :"AIzaSyAj6NGGgE6guMZsGqLod-WTNXcJGwq-DAs",

  });

  if(!isLoaded) return <div>Loading...</div>;
  return <Map/>;

}

const containerStyle = {
  width: '100%',
  height: '100vh'
};


function Map(){
  const center = useMemo(() => ({lat : 24.1502478, lng :120.7159799}) , []);
  return (
  <GoogleMap 
    zoom={18} 
    center={center}
    mapContainerStyle={containerStyle}
  >
    <Marker position= {center}/>

   </GoogleMap> 

    )
}
