import React ,{Component}from 'react';

//let getMacAddress = require("get-mac-address")
const FunctionalComponent=()=>{
    const [count, setCount] = React.useState(5);


        const increase = () => {
            if(count > 10){
               setCount(0) ;
           }else{
            setCount(count+1);}
        }



    return (
        <div style={{margin:'50px'}}>
            <h1>Welcome to Geeks for Geeks </h1>
            <h2>Welcome to Geeks for Geeks </h2>
            <h3>Welcome to Geeks for Geeks </h3>
            <h4>Welcome to Geeks for Geeks </h4>
            <h5>Welcome to Geeks for Geeks </h5>
            <h6>Welcome to Geeks for Geeks </h6>
            <h3>Counter App using Functional Component : </h3>

          <h2>{count}</h2>
          <textarea>type in the word</textarea>
            <button onClick={increase}>Add</button>
            <a href='https://www.google.com' target='_blank'>google</a>
        </div>
    )
}  

const TableHeader = () => {
    return (
      <thead>
        <tr>
          <th>Name</th>
          <th>Job</th>
        </tr>
      </thead>
    )
  }

  const TableBody = () => {
    return (
      <tbody>
        <tr>
          <td>Charlie</td>
          <td>Janitor</td>
        </tr>
        <tr>
          <td>Mac</td>
          <td>Bouncer</td>
        </tr>
        <tr>
          <td>Dee</td>
          <td>Aspiring actress</td>
        </tr>
        <tr>
          <td>Dennis</td>
          <td>Bartender</td>
        </tr>
      </tbody>
    )
  }

  class Table extends Component {
    render() {
      return (
        <div>
        <table>
          <TableHeader />
          <TableBody />
        </table>
        <span>
          <FunctionalComponent />
          <img src='./images/ugly.png' alt='ugly'></img>
          </span>
        </div>
      )
    }
  }

  export default Table;
