import React,{setState} from "react";
import './App.css'

function getLocation ()  {
    if(navigator.geolocation){
      navigator.geolocation.getCurrentPosition(getCoordinates ,handleLocationError);

    }else{
      alert("Geolocaiton is not supported in the browser")
    }
  }

  function getCoordinates (position){
    App.setLat(position.coords.latitude);
    setLng(position.coords.longitude);

}

function handleLocationError(error) {
    switch(error.code){
      case error.PERMISSION_DENIED:
        alert("User denied the request for Geolocation")
        break;
      case error.POSITION_UNAVAILABLE:
        alert('Location information is unavailable.')
        break;
      case error.TIMEOUT:
        alert("The request to get user location timed out.")
        break;
      case error.UNKNOWN_ERROR:
        alert('An unknown error occurred.')
        break;
      default:
        alert('An unknown error occurred.')
    }
  }

function App() {
    const [latitude , setLat] = setState(0);
    const [longitude , setLng] = setState(0);
    const [userAddress , setUrad] = setState(null);

    return (
      <div >
        <h2>React Geolocation Example</h2>

        <h4>html</h4>
        <p>latitude:{latitude}</p>
        <p>longitude:{longitude}</p>
      </div>
    )

}

export default App;

