import React , {useState , useMemo} from "react";
import './App.css'
import { GoogleMap , useLoadScript , Marker} from '@react-google-maps/api';

function App(){
  const [state , setState] = useState({lat:25 , lng:121.5});



  const getLocation = () =>{
    isLoaded
    if(navigator.geolocation){
      navigator.geolocation.getCurrentPosition(getCoordinates , handleLocationError);

    }else{
      alert("Geolocaiton is not supported in the browser")
    }
  }

  const getCoordinates = (position) => {
    setState({
      lat: position.coords.latitude,
      lng:position.coords.longitude
    })
  }


  const handleLocationError = (error) =>{
    switch(error.code){
      case error.PERMISSION_DENIED:
        alert("User denied the request for Geolocation")
        break;
      case error.POSITION_UNAVAILABLE:
        alert('Location information is unavailable.')
        break;
      case error.TIMEOUT:
        alert("The request to get user location timed out.")
        break;
      case error.UNKNOWN_ERROR:
        alert('An unknown error occurred.')
        break;
      default:
        alert('An unknown error occurred.')
    }
  }

  const {isLoaded } = useLoadScript({
    googleMapsApiKey :"AIzaSyAj6NGGgE6guMZsGqLod-WTNXcJGwq-DAs",

  });



  const center = useMemo(() => ({lat :state.lat, lng :state.lng}) , []);

  const containerStyle = {
    width: '100%',
    height: '100vh'
  };

  if(!isLoaded) return <div>Loading...</div>;




    return (
      <div className="App">
        <h2>React Geolocation Example</h2>
        <button onClick={getLocation}>get location</button>
        <h4>html</h4>
        <p>latitude:{state.lat}</p>
        <p>longitude:{state.lng}</p>
        <GoogleMap 
          zoom={18} 
          center={center}
          mapContainerStyle={containerStyle}
        >

<Marker  position= {{lat :state.latitude, lng :state.longitude}}/>
        </GoogleMap>


      </div>
    )


}

export default App;
